bool check(wchar_t x)
{
	if (x >= 65 && x <= 90)return true;
	if (x >= 97 && x <= 122)return true;
	if (x >= 19968 && x <= 40869)return true;
	return false;
}
int root, tot;
class Trie {
public:
	int Fail, id, len;
	unordered_map<wchar_t, int> Child;
	vector<wchar_t> v;
};
Trie t[1000005];
void insert(wstring& str, int ID)
{
	int now = root, i, len = str.length();
	wchar_t x;
	for (i = 0; str[i]; ++i) {
		x = str[i];
		if (x >= 65 && x <= 90)x += 32;
		if (!t[now].Child.count(x)) {
			t[now].Child[x] = ++tot;
			t[now].v.pb(x);
		}
		now = t[now].Child[x];
	}
	t[now].len = i;
	t[now].id = ID;
}
void getFail()
{
	queue<int> q;
	for (auto x : t[0].v) {
		t[t[0].Child[x]].Fail = 0;
		q.push(t[0].Child[x]);
	}
	int now, fail;
	while (!q.empty()) {
		now = q.front();
		q.pop();
		fail = t[now].Fail;
		for (auto x : t[now].v) {
			t[t[now].Child[x]].Fail = t[fail].Child[x];
			q.push(t[now].Child[x]);
		}
		for (auto x : t[fail].v)
			if (t[now].Child.count(x))
				t[now].Child[x] = t[fail].Child[x];
	}
}
int cnt = 0;
void print(wstring& s, int l, int r, wstring& str)
{
	str = L"";
	for (int i = l; i < r; ++i) {
		str += s[i];
		for (auto x : v[i])str += x;
	}
	str += s[r];
}

struct Ans {
public:
	int id, l, r, line;//id表示检测到第几个敏感词，l表示字段的左端，r表示字段的右端,line表示行编码
	wstring org;
}ans[1000005];
int Ans_cnt, Ans_res;
inline bool cmp(Ans a, Ans b) {
	if (a.line != b.line)return a.line < b.line;
	if (a.l != b.l)return a.l < b.l;
	if (a.r != b.r)return a.r < b.r;
	return a.id < b.id;
}
bool operator !=(Ans a, Ans b) {
	if (a.id != b.id)return true;
	if (a.l != b.l)return true;
	if (a.r != b.r)return true;
	if (a.line != b.line)return true;
	return false;
}
void query(wstring& s, int len, int Line, int mode)
{
	int now = 0;
	wchar_t x;
	for (int i = 0; i < len; ++i) {
		x = s[i];
		if (x >= 65 && x <= 90)x += 32;
		now = t[now].Child[x];
		for (int j = now; j && t[j].len; j = t[j].Fail) {
			ans[++Ans_cnt].id = t[j].id;
			ans[Ans_cnt].l = i - t[j].len + 1;
			ans[Ans_cnt].r = i;
			if (mode)ans[Ans_cnt].l = pos[ans[Ans_cnt].l],
				ans[Ans_cnt].r = pos[ans[Ans_cnt].r];
			ans[Ans_cnt].line = Line;
			print(str, ans[Ans_cnt].l, ans[Ans_cnt].r, ans[Ans_cnt].org);
			//t[j].len = 0;
		}
	}
}

unordered_map<wchar_t, wstring>Hanzi2Pinyin;
unordered_map<wchar_t, wstring>Hanzi_Chaizi;
unordered_map<wchar_t, wstring>Hanzi_Fanti;
void init_Pinyin();
void init_Chaizi();
void init_Fanti();
class Word {
public:
	wstring s[5];
	bool b[5];
	//s[0]字本身
	//s[1]拼音
	//s[2]首字母
	//s[3]拆偏旁
	//s[4]繁体字
	void getPinyin() {
		//wcout << s[0] << endl;
		s[1] = Hanzi2Pinyin[s[0][0]];
		s[2] = L"";
		s[2] += s[1][0];
		//wcout << s[1] << " " << s[2] << endl;
		b[1] = b[2] = true;
	}
	void getChaizi() {
		if (Hanzi_Chaizi.count(s[0][0]))s[3] = Hanzi_Chaizi[s[0][0]], b[3] = true;
		else b[3] = false;
		//wcout << s[3] << endl;
	}
	void getFanti() {
		if (Hanzi_Fanti.count(s[0][0]))s[4] = Hanzi_Fanti[s[0][0]], b[4] = true;
		else b[4] = false;
		//wcout << s[4] << endl;
	}
}w[10000], xieyin;
int siz;
void Dictionary(int x, wstring str, int len, int ID)
{
	if (x >= len) {
		dic[++siz] = str;
		id[siz] = ID;
		return;
	}
	wstring tmp;
	for (int i = 0; i < 5; ++i)
		if (w[x].b[i]) {
			tmp = str + w[x].s[i];
			Dictionary(x + 1, tmp, len, ID);
		}
}
void expansion(wstring str, int ID) {
	int len = str.length();
	for (int i = 0; i < len; ++i) {
		w[i].s[0] = L"";
		w[i].s[0] += str[i];
		w[i].b[0] = true;
		w[i].getPinyin();
		w[i].getChaizi();
		w[i].getFanti();
	}
	Dictionary(0, L"", len, ID);//生成敏感词字典
}

