	assert(w);

	len = 0;
	wstring ch;
	for (int line = 1; getline(r1, ch); ++line) {
		for (int i = 0; i < len; ++i)v[i].clear();
		len = 0;
		str = L"";
		int tmplen = ch.length();
		for (int i = 0; i < tmplen; ++i) {
			x = ch[i];
			if (check(x))str += x, ++len;
			else {
				if (len > 0)v[len - 1].pb(x);
			}
			//wcout << x;
		}
		query(str, len, line, 0);
		Len = 0; py_str = L"";
		for (int i = 0; i < len; ++i) {
			if (str[i] >= 19968 && str[i] <= 40869) {
				xieyin.s[0] = L"";
				xieyin.s[0] += str[i];
				xieyin.getPinyin();
				int tmp = Len;
				Len += xieyin.s[1].length();
				for (int j = tmp; j < Len; ++j)pos[j] = i;
				py_str += xieyin.s[1];
			}
			else {
				py_str += str[i];
				pos[Len++] = i;
			}
		}
		query(py_str, Len, line, 1);
	}
	sort(ans + 1, ans + Ans_cnt + 1, cmp);
	for (int i = 1; i <= Ans_cnt; ++i)
		if (i == 1 || ans[i] != ans[i - 1])++Ans_res;
	w << "Total: " << Ans_res << endl;
